import PropTypes from 'prop-types';
import React from 'react';
import { useLocation } from 'react-router-dom';

const CompanyDetails = () => {
  const location = useLocation();
  const { company } = location.state;

  return (
    <>
      <div className="container hero flex items-center p-3">
        <img
          src={company.image}
          alt={company.companyName}
          className="logo w-50"
        />
        <div className="w-100 text-center">
          <h1 className="font-bold">{company.symbol}</h1>
          <span>{company.price}</span>
        </div>
      </div>
      <h5
        className="container mt-2 pb-1"
        style={{ background: 'var(--dark-pink)' }}
      >
        {company.companyName}
        &nbsp;info
      </h5>
      <div className="company__data">
        <ListGroup>
          {Object.keys(company)
            .filter((value) => value !== 'image')
            .map((value) => (
              <ListGroup.Item
                key={value}
                className="company__data-item border-0"
              >
                <div className="flex justify-between">
                  <span>{value}</span>
                  <div className="flex items-center">
                    <span className="p-3">{company[value]}</span>
                    <FaArrowRight
                      className="FaArrowRight"
                      size={22}
                      color="white"
                    />
                  </div>
                </div>
              </ListGroup.Item>
            ))}
        </ListGroup>
      </div>
    </>
  );
};

CompanyDetails.propTypes = {
  company: PropTypes.shape({
    image: PropTypes.string.isRequired,
    companyName: PropTypes.string.isRequired,
    symbol: PropTypes.string.isRequired,
    price: PropTypes.number.isRequired,
  }).isRequired,
};
export default CompanyDetails;
