import React, { useState } from 'react';
import { FaAngleLeft, FaMicrophone, FaCog } from 'react-icons/fa';
import { useDispatch } from 'react-redux';
import CompaniesList from '../list/FinancialList';
import { useLocation } from 'react-router-dom'; // Import the useLocation hook
import { handleBackButtonClickAction } from '../../redux/actions/categoriesActions';

const Header = () => {
  const [showCompaniesList, setShowCompaniesList] = useState(false);
  const dispatch = useDispatch();
  const location = useLocation(); // Get the current location

  const handleBackButtonClick = () => {
    dispatch(handleBackButtonClickAction());
    setShowCompaniesList(true);
  };

  return (
    <div>
      <div>
        <nav className="bg-dark-pink">
          <div className="container mx-auto flex items-center justify-between py-4">
            <button type="button" className="text-body-primary" onClick={handleBackButtonClick}>
              <FaAngleLeft className="text-body-primary" size={26} color="white" />
            </button>
            <h3 className="mb-0 text-white">finance</h3>
            <div className="flex">
              <FaMicrophone className="text-white" size={22} />
              <FaCog className="text-white ml-8" size={22} />
            </div>
          </div>
        </nav>
      </div>
      {showCompaniesList && <CompaniesList />}
    </div>
  );
};

export default Header;
